// src/pages/AskQuestion.js
import { useState } from "react";

export default function AskQuestion() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setAnswer("");

    const res = await fetch("/api/generate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ question }),
    });

    const data = await res.json();
    setAnswer(data.result || "Something went wrong. Please try again.");
    setLoading(false);
  };

  return (
    <main style={{ maxWidth: 700, margin: '2rem auto', padding: '0 1rem' }}>
      <h2>Ask Your Question</h2>
      <p>Enter your real-life question to receive wisdom grounded in Indian spirituality.</p>

      <form onSubmit={handleSubmit}>
        <textarea
          placeholder="Type your question here..."
          value={question}
          onChange={e => setQuestion(e.target.value)}
          rows={5}
          required
          style={{ width: '100%', padding: '0.7rem', fontSize: '1rem', marginBottom: '1rem' }}
        />
        <button type="submit" disabled={loading} style={{
          backgroundColor: '#0070f3', color: 'white', border: 'none',
          padding: '0.8rem 1.5rem', borderRadius: 6, cursor: 'pointer',
          fontSize: '1rem'
        }}>
          {loading ? "Generating..." : "Submit"}
        </button>
      </form>

      {answer && (
        <div style={{
          marginTop: '2rem',
          padding: '1rem',
          backgroundColor: '#f1fdf4',
          border: '1px solid #a5d6a7',
          borderRadius: 6
        }}>
          <h3>Spiritual Guidance:</h3>
          <p>{answer}</p>
        </div>
      )}
    </main>
  );
}
